		assert colorized.equals('true')
		if(color.size()>1){
//			log.info id.toString()
			if (hasMoreColors.equals('true')){
//				log.info "Pass"	
	}
			else
				log.info "Failed for product - '"+id.toString()+"'"
		}
	}
}

